mapper.update:
UPDATE TOUR_FREEBOARD

	FREEBOARD_TITLE = #{freeBoardTitle},
	FREEBOARD_CONTENT = #{freeBoardContent},
	FREEBOARD_IMG = #{freeBoardImg}
	FREEBOARD_THUMB = #{freeBoardThumb}

WHERE FREEBOARD_ID = #{freeBoardId}
  AND FREEBOARD_WRITER = #{freeBoardWriter}

UPDATE TOUR_FREEBOARD
   SET FREEBOARD_HIT = FREEBOARD_HIT + 1
 WHERE FREEBOARD_ID = #{freeboardId}
