mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROWN, A.*, C.CATEGORY_NAME AS CATEGORYNAME FROM( SELECT AQUARIUM_ID AS TICKETID, AQUARIUM_NAME AS TICKETNAME, AQUARIUMIMG AS IMG, 21 AS CATEGORYID FROM TOUR_AQUARIUM UNION ALL SELECT THEMEPARK_ID AS TICKETID, THEMEPARK_NAME AS TICKETNAME, THEMEPARK_IMG AS IMG, 22 AS CATEGORYID FROM TOUR_THEMEPARK UNION ALL SELECT WATERPARK_ID AS TICKETID, WATERPARK_NAME AS TICKETNAME, WATERPARK_IMG AS IMG, 23 AS CATEGORYID FROM TOUR_WATERPARK ) A, TOUR_CATEGORY C WHERE A.CATEGORYID = C.CATEGORY_ID
AND A.CATEGORYID = #{categoryId}
) WHERE ROWN BETWEEN #{startPage} AND #{endPage}

SELECT *

	FROM TOUR_AQUARIUM
   WHERE AQUARIUM_ID = #{ticketId}


	FROM TOUR_THEMEPARK
   WHERE THEMEPARK_ID = #{ticketId}


	FROM TOUR_WATERPARK
   WHERE WATERPARK_ID = #{ticketId}

SELECT COUNT(*) AS TOTALCOUNT FROM ( SELECT ROWNUM AS ROWN, A.*, C.CATEGORY_NAME AS CATEGORYNAME FROM( SELECT AQUARIUM_ID AS TICKETID, AQUARIUM_NAME AS TICKETNAME, AQUARIUMIMG AS IMG, 21 AS CATEGORYID FROM TOUR_AQUARIUM UNION ALL SELECT THEMEPARK_ID AS TICKETID, THEMEPARK_NAME AS TICKETNAME, THEMEPARK_IMG AS IMG, 22 AS CATEGORYID FROM TOUR_THEMEPARK UNION ALL SELECT WATERPARK_ID AS TICKETID, WATERPARK_NAME AS TICKETNAME, WATERPARK_IMG AS IMG, 23 AS CATEGORYID FROM TOUR_WATERPARK ) A, TOUR_CATEGORY C WHERE A.CATEGORYID = C.CATEGORY_ID
AND A.CATEGORYID = #{categoryId}
)
