mapper.select:
SELECT T.* FROM( SELECT ROWNUM AS ROWN, A.*, C.CATEGORY_NAME FROM( SELECT BUS_ID AS TID, BUS_START AS TSTART, BUS_FINISH AS TFINISH, BUS_DEPTIME AS DEPTIME, 11 AS CATEGORYID FROM TOUR_BUS UNION ALL SELECT TRAIN_ID AS TID, TRAIN_START AS TSTART, TRAIN_FINISH AS TFINISH, TRAIN_DEPTIME AS DEPTIME, 12 AS CATEGORYID FROM TOUR_TRAIN UNION ALL SELECT AIRPLANE_ID AS TID, AIRPLANE_START AS TSTART, AIRPLANE_FINISH AS TFINISH, AIRPLANE_DEPTIME AS DEPTIME, 13 CATEGORYID FROM TOUR_AIRPLANE ) A, TOUR_CATEGORY C WHERE A.CATEGORYID = C.CATEGORY_ID
AND A.CATEGORYID = #{categoryId}
)T WHERE T.ROWN BETWEEN #{startPage} AND #{endPage}

SELECT *

  FROM TOUR_BUS
 WHERE BUS_ID = #{tId}


  FROM TOUR_TRAIN
 WHERE TRAIN_ID = #{tId}


  FROM TOUR_AIRPLANE
 WHERE AIRPLANE_ID = #{tId}

SELECT COUNT(*) AS TOTALCOUNT FROM( SELECT ROWNUM AS ROWN, A.*, C.CATEGORY_NAME FROM( SELECT BUS_ID AS TID, BUS_START AS TSTART, BUS_FINISH AS TFINISH, BUS_DEPTIME AS DEPTIME, 11 AS CATEGORYID FROM TOUR_BUS UNION ALL SELECT TRAIN_ID AS TID, TRAIN_START AS TSTART, TRAIN_FINISH AS TFINISH, TRAIN_DEPTIME AS DEPTIME, 12 AS CATEGORYID FROM TOUR_TRAIN UNION ALL SELECT AIRPLANE_ID AS TID, AIRPLANE_START AS TSTART, AIRPLANE_FINISH AS TFINISH, AIRPLANE_DEPTIME AS DEPTIME, 13 CATEGORYID FROM TOUR_AIRPLANE ) A, TOUR_CATEGORY C WHERE A.CATEGORYID = C.CATEGORY_ID
AND A.CATEGORYID = #{categoryId}
)T
